        category:  { _id: "5b21ca3eeb7f6fbccd471823", name: "Fishlet" },
        numberInStock: 3,
        price: 300,
        netWeight: 2
    }
];

export function getFishes(){
    return fishes;
}

export function getFish(id){
    return fishes.find(f => f._id === id);
}

export function saveFish(fish){
    let fishInDb = fishes.find(f => f._id === fish._id) || {};
    fishInDb.name = fish.name;
    fishInDb.category = categoriesAPI.categories.find(f => f._id === fish.categoryId);
    fishInDb.numberInStock = fish.numberInStock;
    fishInDb.price = fish.price;
    fishInDb.netWeight = fish.netWeight;

    if (!fishInDb._id){
        fishInDb._id = Date.now();
        fishes.push(fishInDb);
    }

    return fishInDb;
}

export function deleteFish(id){
    let fishInDb = fishes.find(f => f._id === id);
    fishes.splice(fishes.indexOf(fishInDb), 1);
    return fishInDb;
}
