/* eslint-disable no-unused-vars */
import React, { Component } from 'react';
import Counters from './components/counters';
import Navbar from './components/navbar';
import { getCategories } from "./data/fakeCategoryList";
import { getWeight } from "./data/fakeFishList";

export class App extends Component{


  state = {
    headers: [
        {
            id:1, 
            category: getCategories(), 
            value: 9
        }
    ],

    counters: [
        {
            id: 1, 
            value: 5,
            capacity: 0,
            totalWeight: getWeight
        }
    ],

    cards: [
        {id: 1, name: "Ilish 2kg"},
        {id: 2, name: "Shutki 5kg"}
    ]
};

// increment/increase qty method 
incrementHandler = counter => {
    const counters = [...this.state.counters];
    const index = counters.indexOf(counter);
    counters[index] = { ...counter };
    counters[index].capacity++;
    this.setState({ counters });
};

// delete/increment item method 
deleteHandler = (cardID) => {
    // if(this.fish === undefined) {return} //catcher to handle error
    const cards = this.state.cards.filter(c => c.id !== cardID);
    this.setState({ cards });
    console.log("clicked to ", cardID);
    // if (this.setState({cartCount: this.state.cartCount - 1})) return 0;
};

// undo/reset method 
resetHandler = () => {
    const counters = this.state.counters.map(c => {
        c.capacity = 0;
        return c;
    });

    this.setState({ counters });
}


  render(){
    return (//here Counter comp will need to render  

      <React.Fragment>
        <Navbar counters={this.state.counters.totalWeight}/>
        <main>
          <Counters 
          counters={this.state.counters}
          headers={this.state.headers}
          cards={this.state.cards}
          onIncreament={this.incrementHandler}
          onDelete={this.deleteHandler}
          onReset={this.resetHandler} />
        </main>
      </React.Fragment>
    );
  }
}

export default App;
