/* eslint-disable no-unused-vars */
import React from 'react';
import Card from './cards';
import Counter from './counter';

class Counters extends React.Component {

    state = {
        counters: [
            {id: 1, value: 0}
        ],

        cards: [
            {id: 1, value: 1},
            {id: 2, value: 2},
            {id: 3, value: 3}
        ]
    };

    deleteHandler = (cardID) => {
        // if(this.fish === undefined) {return} //catcher to handle error

        const cards = this.state.cards.filter(c => c.id !== cardID);
        this.setState({ cards });
        console.log("clicked to ", cardID);


        // if (this.setState({cartCount: this.state.cartCount - 1})) return 0;
    };

    render() { 

        return (

        <div className="container">  
            <div className="row">


                <div className="col-md-6 col-12">
                    {this.state.counters.map(counter => 
                    <Counter key={counter.id} />)
                    }                
                </div>

                <div className="col-md-6 col-12">
                    {this.state.cards.map(card => 
                    <Card key={card.id} value={card.value} onDelete={this.deleteHandler}/>) //prop inserted, declated in cards.js
                    }
                </div>

            </div>
        </div>     
        );
    }
}

export default Counters;
