/* eslint-disable no-unused-vars */
import React from 'react';
import { getCategories } from "../data/fakeCategoryList";
import Card from './cards';
import Counter from './counter';
import Header from './header';

class Counters extends React.Component {

    state = {
        headers: [
            {
                id:1, 
                category: getCategories(), 
                value: 9
            }
        ],

        counters: [
            {id: 1, value: 0}
        ],

        cards: [
            {id: 1, name: "Ilish 2kg"},
            {id: 2, name: "Shutki 5kg"}
        ]
    };

    // increment/increase qty method 
    incrementHandler = header => {
        const headers = [...this.state.headers];
        const index = headers.indexOf(header);
        headers[index] = { ...header };
        headers[index].value++;
        this.setState({ headers });
   };

    // delete/increment item method 
    deleteHandler = (cardID) => {
        // if(this.fish === undefined) {return} //catcher to handle error
        const cards = this.state.cards.filter(c => c.id !== cardID);
        this.setState({ cards });
        console.log("clicked to ", cardID);
        // if (this.setState({cartCount: this.state.cartCount - 1})) return 0;
    };

    // undo/reset method 
    handleReset = () => {
        const cards = this.state.cards.map(c => {
            c.id = c;
            return c.id;
        });
        console.log(cards);
        this.setState({ cards });
    }

    render() { 

        return (

        <div className="container">  
            <div className="row">

                <div className="col-md-12 col-12">
                    {this.state.headers.map(header => 
                    <Header key={header.id} 
                    header={header} //will receive all state values
                    onClick={this.incrementHandler}
                    category = {this.getCategories}/>)}                
                </div>  

                <div className="col-md-6 col-12">
                    {this.state.counters.map(counter => 
                    <Counter key={counter.id} 
                    counter={counter}/>)}                
                </div>

                <div className="col-md-6 col-12">

                    <button className="btn btn-sm btn-success" 
                        onClick= {this.handleReset}> 
                        <i className="fa fa-undo"></i> Undo items
                    </button>

                    {this.state.cards.map(card => 
                    <Card key={card.id} 
                    card={card}
                    onDelete={this.deleteHandler}
                    onClick= {this.handleReset}/>) //prop inserted, declated in cards.js
                    }
                </div>

            </div>
        </div>     
        );
    }
}

export default Counters;
