/* eslint-disable no-unused-vars */
import React from 'react';
import Card from './cards';
import Counter from './counter';
import Header from './header';
import Reviews from './reviews';

class Counters extends React.Component {

    state = {

        headers: [
            {id:1, value: 4}
        ],

        counters: [
            {id: 1, value: 0}
        ],

        cards: [
            {id: 3, value: 3}
        ]
    };


    render() { 

        const [{value}] = this.state.cards;

        return (

        <div className="container">  
            <div className="row">

                <div className="col-md-12 col-12">
                    {this.state.headers.map(header => 
                    <Header key={header.id} />)
                    }
                    <div className="d-block float-right rounded shadow p-3">
                        <i className="fa fa-shopping-cart text-primary mr-2"></i>
                        <span className="badge badge-light text-black"> 
                            {value} 
                        </span>
                    </div>                  
                </div>

                <div className="col-md-6 col-12">
                    {this.state.counters.map(counter => 
                    <Counter key={counter.id} />)
                    }                
                </div>

                <div className="col-md-6 col-12">
                    {this.state.cards.map(card => 
                    <Card key={card.id} value={card.value} selected/>)
                    }
                </div>

                <div className="col-md-12 col-12">
                    <Reviews />
                </div>




            </div>
        </div>     
        );
    }
}

export default Counters;
