];

export function getSubTotal(){
    let price = fishes.map(p => p.price);
    let netWeight = fishes.map(nw => nw.price);

    let subTotal = (price, netWeight) =>{
       return price * netWeight;
    } 
    return subTotal;
}

console.log(getSubTotal());


