import React, { Component } from 'react';


class Counter extends React.Component {
    state = {
        mainTitle: "Machbazaar.com",
        subTitle: "Online fish selling platform.",
        cartCount: 2
    };
    render(){ 
        return (
            //we can add <React.Fragment> instead of <div></div> if we don't want this blank div
            <div className="container">
                <div className="row">
                    <div className="col-md-6 text-left mt-4">
                        <h1 className="font-weight-bold">{this.state.mainTitle}</h1>
                        <h4>{this.state.subTitle}</h4>
                    </div>  
                    <div className="col-md-6 text-left mt-4">
                        <a type="button" className="btn btn-success text-white float-right">
                        <i className="fas fa-shopping-cart"></i>&nbsp; <span>{this.formatCount()}</span> Cart 
                        </a>  
                    </div>    
                </div>
            </div>
        ); 
    }

    formatCount(){
        const {cartCount} = this.state; //destructuring happens
        return cartCount === 0 ? "Empty" : cartCount;
    }    


}

export default Counter;

