/* eslint-disable no-unused-vars */
import React from 'react';
import Card from './cards';
import Counter from './counter';
import Header from './header';
import Reviews from './reviews';
import Tables from './table';

class Counters extends React.Component {


    render() {
        const {length: count} = this.props.counter;
        return (

        <div className="container mt-4"> 

             <h4 className="text-left">Your inventory has {count} capacity counter(s)</h4>
                <div className="header-type-div mb-4">
                    {this.props.headers.map(header => 
                    <Header key={header.id} 
                    header={header} //will receive all state values
                    category = {this.getCategories}/>)}                
                </div>  

                <div className="d-flex justify-content-start">

                        <div className="d-flex justify-content-around counters-div">
                            {this.props.counter.map(counter => 
                            <Counter key={counter.id}
                            counter={counter}
                            onIncrement={this.props.onIncrement}
                            onDelete={this.props.onDelete}
                            onReset={this.props.onReset}
                            /> )}


                            <div className="reset-div">
                                <button className="btn btn-sm btn-dark" 
                                    onClick= {this.props.onReset}> 
                                    <i className="fa fa-undo"></i> Reset capacity
                                </button>
                            </div>
                        </div>      

                        <div className="card-div">
                            <h4 className="mb-4">Related data</h4>
                            {this.props.cards.map(card => 
                            <Card key={card.id} 
                            card={card}
                            onDelete={this.props.onDelete}/>) //prop inserted, declated in cards.js
                            }
                        </div>
                </div>                

                <div className="col-md-7 col-12">
                    {this.props.tables.map(table => 
                    <Tables key={table.id} 
                    tables={table}
                    onDelete={this.props.onDelete}/>)}
                </div> 

                <div className="col-md-5 col-12">
                    <Reviews />
                </div>

        </div>     
        );
    }
}

export default Counters;
