];

export function getFishes(){
    return fishes;
}

//lets sum the total (weight*unit price)

export const getSubTotal = function (){
    let netWeight = fishes.map(nw => nw.netWeight);
    let price = fishes.map(p => p.price);
    let subTotal = () => netWeight*price;
    let subTotalArrOld = [];
    let subTotalArrNew = subTotalArrOld.push(subTotal);

    return subTotalArrNew;
}

export const SUBTOTAL = getSubTotal();

export const getGrandTotal = function(){ 
    const totalPrice = SUBTOTAL.reduce((previousValue, currentValue) => {
