    fishInDb.price = fish.price;

    if (!fishInDb.fishID){
        fishInDb.fishID = Date.now();
        fishes.push(fishInDb);
    }

    return fishInDb;
}

export function deleteFish(id){
    let fishInDb = fishes.find(f => f.fishID === id);
    fishes.splice(fishes.indexOf(fishInDb), 1);
    return fishInDb;
}
