import React, { Component } from 'react';
import { getFishes } from "../data/fakeFishList";
import { getCategories } from "../data/fakeCategoryList";


class Counter extends React.Component {
    state = {
        mainTitle: "Machbazaar.com",
        subTitle: "Online fish selling platform.",
        imageUrl: "https:/picsum.photos/90",
        cartCount: 0,
        reviews: ['Good one', 'Bad one', 'Satisfied', 'Loved it'],
        categories: getCategories(),
        fishes: getFishes()
    };

    styles = {
        borderRadius: "50%",
        marginRight: "15px"
    };

    //eventHandler for the increment btn
    //binding a func using arrow func so that we don't need to create constructor
    incrementHandler = fish => {
        // console.log(product);
        this.setState({ cartCount: this.state.cartCount + 1 });
        //setState is a built-in method to notify react that state prop value is gonna be changed
        //react will then schedule a async call to the render(), i,e this is going to be happened in the future
    };

    decrementHandler = fish => {
        // if(this.fish === undefined) {return} //catcher to handle error

        const fishes = this.state.fishes.filter(f => f.fishID !== fish.fishID);
        this.setState({ fishes });

        if (this.setState({cartCount: this.state.cartCount - 1})) return 0;

    };

    render(){ 

        const {length: count} = this.state.fishes;



        return (
            //react element starts
            //we can add <React.Fragment> instead of <div></div> if we don't want this blank div

            <div className="container">
                <div className="row">
                    <div className="col-md-9 col-12 mt-4 d-flex justify-content-start">
                        <img style={this.styles} className="border" src={this.state.imageUrl} alt=""/> 
                        <div>
                            <h1 className="font-weight-bold">
                                {this.state.mainTitle}
                            </h1> 
                            <h4>{this.state.subTitle}</h4>
                        </div>
                    </div>

                    <div className="col-md-3 col-12 text-right mt-4">
                        <a type="button" className={this.btnDanger()}>
                            <i className="fa fa-shopping-cart"></i>&nbsp;&nbsp; 
                            <span className="badge badge-light">{this.state.cartCount} </span>
                        </a>  
                    </div> 

                    <div className="col-12 mt-5 d-flex justify-content-start align-items-center">
                        <h3 className="mr-4">Categories</h3>

                        {this.categoryMap()}

                    </div>

                    {/* Products list */}
                    <div className="col-md-7 col-12 mt-5">
                        {/* <ul>
                            {this.tagMap()}
                        </ul>  */}
                        <h4 className="mb-4">Your cart has {count} item(s)</h4>

                        <table className="table table-striped table-responsive">
                            <thead>
                                <tr key={""}>
                                    <th>Name</th>
                                    <th>Category</th>
                                    <th>Weight(kg)</th>
                                    <th>Unit Price</th>
                                    <th>Sub</th>
                                    <th></th>
                                    <th></th>
                                </tr>
                            </thead>

                            <tbody>
                                {this.state.fishes.map(fish => ( 
                                <tr key={fish.fishID}>
                                    <td>{fish.title}</td>
                                    <td>{fish.category.name}</td>
                                    <td>{fish.netWeight}</td>
                                    <td>{fish.price}</td>
                                    <td>{fish.netWeight*fish.price}</td>
                                    <td>{<button onClick={() => this.incrementHandler(fish)} className="btn btn-sm btn-warning">+</button>}</td>
                                    <td>{<button onClick={() => this.decrementHandler(fish)} className="btn btn-sm btn-danger"> <i className="fa fa-trash"></i></button>}</td>
                                </tr>
                                ))}


                                <tr>
                                    <td></td>
                                    <td></td>
                                    <td></td>
                                    <td>Grand Total:</td>

                                    <td>
                                    {
                                    this.getGrandTotal()
                                    }
                                    </td>

                                    <td></td>
                                    <td></td>
                                    <td></td>
                                </tr>


                            </tbody>


                        </table>
                    </div>

                    {/* Review list */}
                    <div className="col-md-5 col-12 mt-5">
                        <h4>Reviews{this.state.reviews.length != 0 && <span> ({this.state.reviews.length})</span>}</h4>
                        {/* truthy,falsy applied using && after condition which can be used to conditional render  */}
                        {this.state.reviews.length === 0 && <p>There are no reviews.</p>}
                        {this.reviewMap()}
                    </div>

                </div>
            </div>
        ); 
    }

    //lets sum the grand total price
    getGrandTotal(){
        let netWeightPrice = this.fish.netWeight * this.fish.price;
        let netWeightPriceArr; 
        netWeightPriceArr.push(netWeightPrice);
        const totalPrice =  netWeightPriceArr.reduce((previousValue, currentValue) => {
            return previousValue+currentValue; 
        }, 0);
        return totalPrice;
    }


    //conditional rendering for review list 
    //another conditional rendering is shown where the func is called using truthy,falsy
    //key={} is required to assign unique key value to each list for backend integration
    //another way of map() is showed
    reviewMap(){
        if (this.state.reviews.length === 0) return <p>Please add reviews.</p>;
        return <ul>{this.state.reviews.map(reviewElem => <li key={reviewElem}>{reviewElem}</li>)} </ul>;
    }    

    //map() - categories
    categoryMap(){
        return <div>{this.state.categories.map(elem => <button className="btn btn-light border shadow mb-2 mr-4"> {elem.name} </button>)}</div>     
    }

    //map() - tags
    // tagMap(){

    //     const incrementBtn = 
    //     <div className="text-right"> 
    //         <button onClick={() => this.incrementHandler(this.product)} className="btn btn-sm btn-warning mr-2">+</button> 
    //         <button className="btn btn-sm btn-danger"> <i className="fa fa-trash"></i></button> 
    //     </div>;

    //     const tagMapper = this.state.fishes.map(fish => 
    //         <li key={fish} className="card p-3 mb-2"> {fish} {incrementBtn}</li>);
    //         return tagMapper;
    // }

    btnDanger(){
        return this.state.cartCount===0 ? 'btn btn-lg btn-danger shadow text-white' 
        : 'btn btn-lg btn-success shadow text-white';
    }

    formatCount(){
        const {cartCount} = this.state; //destructuring happens
        const emptySpan = <span className="badge badge-light">Empty</span>;
        return cartCount === 0 ? emptySpan : cartCount;
    }    
}



export default Counter;

