/* eslint-disable no-unused-vars */
import React from 'react';
import Card from './cards';
import Counter from './counter';

class Counters extends React.Component {

    state = {

        headers: [
            {id:1, value: 4}
        ],

        counters: [
            {id: 1, value: 0}
        ],

        cards: [
            {id: 3, value: 3}
        ],

        reviews: [
            {id: 4}
        ]
    };

    deleteHandler = (fish) => {
        // if(this.fish === undefined) {return} //catcher to handle error

        const fishes = this.state.fishes.filter(f => f.fishID !== fish.fishID);
        this.setState({ fishes });

        // if (this.setState({cartCount: this.state.cartCount - 1})) return 0;

    };

    render() { 

        return (

        <div className="container">  
            <div className="row">


                <div className="col-md-6 col-12">
                    {this.state.counters.map(counter => 
                    <Counter key={counter.id} onDelete={this.deleteHandler}/>)
                    }                
                </div>

                <div className="col-md-6 col-12">
                    {this.state.cards.map(card => 
                    <Card key={card.id} value={card.value}/>) //prop inserted, declated in cards.js
                    }
                </div>

            </div>
        </div>     
        );
    }
}

export default Counters;
