/* eslint-disable jsx-a11y/anchor-is-valid */
import React from 'react';

class Navbar extends React.Component {
    render() { 
        return (
            <nav className="navbar navbar-light bg-light">
                <a href="#" className="navbar-brand">Machbazaar Inventory
                </a>

                <div className="budget-select mb-2"> 
                    <button onClick={() => this.props.onIncrement(this.props.counter)} type="button" className="btn btn-sm btn-default">
                        Increase your table capacity (+)
                    </button> 

                    <span className="badge badge-dark ml-2"> {this.props.counter.capacity} </span> 
                </div>
            </nav>


        );
    }
}

export default Navbar;
