/* eslint-disable no-unused-vars */
import React from 'react';


export class Card extends React.Component {
    state = {
        imageUrl: "https:/picsum.photos/90",
        cartValue: this.props.value //declared props that was inserted in counters.js
    };

    styles = {
       width: "auto"
    };

    //eventHandler for the increment btn
    //binding a func using arrow func so that we don't need to create constructor
    //setState is a built-in method to notify react that state prop value is gonna be changed
    //react will then schedule a async call to the render(), i,e this is going to be happened in the future
    incrementHandler = () => {
        this.setState({ cartValue: this.state.cartValue + 1 });
   };

    render(){ 

        return (

            //react element starts
            //we can add <React.Fragment> instead of <div></div> if we don't want this blank div

            <div className="container">
                <div className="row">

                    <div className="col-md-6 col-12 mt-5">
                        <h4 className="mb-4">Related items</h4>
                        <div className="card">

                            <div className="card-body">
                                <h5 className="card-title">Ilish 2kg</h5>
                                <button onClick={this.incrementHandler}  className="btn btn-primary">
                                <i className="fa fa-shopping-cart mr-2"></i> Add to cart
                                </button>

                                <button onClick={this.deleteHandler}  className="btn btn-danger">
                                <i className="fas fa-trush mr-2"></i> Add to cart
                                </button>
                            </div>
                        </div>
                    </div>

                    <div className="col-md-6 col-12">
                        <button type="button" href="#" className="btn btn-warning">
                            <i className="fa fa-shopping-cart"></i>&nbsp;&nbsp; 
                            <span className="badge badge-light"> {this.state.cartValue}</span>
                        </button>  
                    </div>

                </div>
            </div>
        ); 
    }




    formatCount(){
        const {cartValue} = this.state; //destructuring happens
        const emptySpan = <span className="badge badge-light">Empty</span>;
        return cartValue === 0 ? emptySpan : cartValue;
    }    


}


export default Card;
