/* eslint-disable no-unused-vars */
import React from 'react';
import Card from './cards';
import Counter from './counter';
import Header from './header';
import Reviews from './reviews';

class Counters extends React.Component {


    render() { 

        return (

        <div className="container">  
            <div className="row">

                <div className="col-md-12 col-12">
                    {this.props.headers.map(header => 
                    <Header key={header.id} 
                    header={header} //will receive all state values
                    category = {this.getCategories}/>)}                
                </div>  


                <div className="col-md-6 col-12">
                    {this.props.counters.map(counter => 
                    <Counter key={counter.id} 
                    counter={counter}
                    onIncrement={this.props.onIncreament}/>)}        


                <button className="btn btn-sm btn-dark ml-3" 
                    onClick= {this.props.onReset}> 
                    <i className="fa fa-undo"></i> Reset capacity
                </button>

                </div> 

                <div className="col-md-6 col-12">

                    {this.props.cards.map(card => 
                    <Card key={card.id} 
                    card={card}
                    onDelete={this.props.onDelete}/>) //prop inserted, declated in cards.js
                    }
                </div>

                <div className="col-md-6 col-12">
                    <Reviews />
                </div>

            </div>
        </div>     
        );
    }
}

export default Counters;
