import React from 'react';

class Counter extends React.Component {
    render() { 
        return (
            <div className="budget-select mb-2"> 
                <button className="btn btn-sm btn-dark" 
                    onClick= {this.props.onReset}> 
                    <i className="fa fa-undo"></i> Reset capacity
                </button>

                <button className="btn btn-sm btn-primary ml-4" 
                    onClick={() => this.props.onIncreament(this.props.counter)}>
                    Set
                </button> 

                <span className="badge badge-dark ml-2"> {this.props.counter.capacity} </span> 
            </div>
        );
    }
}

export default Counter;
